pragma solidity ^0.4.23;

import "./OraclizeAPI.sol";

/**
 * @title Ownable
 * @dev The Ownable contract has an owner address, and provides basic authorization control
 * functions, this simplifies the implementation of "user permissions".
 */
contract Ownable {

  address public owner;

  /**
  * @dev The Ownable constructor sets the original `owner` of the contract to the sender
  * account.
  */
  constructor() public {
    owner = msg.sender;
  }

  /**
  * @dev Throws if called by any account other than the owner.
  */
  modifier onlyOwner() {
    require(msg.sender == owner);
    _;
  }

  /**
  * @dev Allows the current owner to transfer control of the contract to a newOwner.
  * @param newOwner The address to transfer ownership to.
  */
  function transferOwnership(address newOwner) onlyOwner public {
    require(newOwner != address(0));
    owner = newOwner;
  }

}

contract BWCToken is usingOraclize, Ownable {

  struct Contributor {
    address contributor;
    uint256 amount;
  }

  enum State {
    Sale,
    Paused,
    Finished
  }

  string public constant name = "BWCToken";
  string public constant symbol = "BWC";
  uint32 public constant decimals = 18;

  uint256 public constant totalSupply = 1000000000 * (10 ** uint256(decimals));
  uint256 public constant softCap = 15000;
  uint256 public constant hardCap = 100000;

  uint256 public totalRaisedUSD = 0;
  uint256 public totalRaisedToken = 0;

  State currentState = State.Sale;
  mapping(address => uint256) balances;
  mapping(bytes32 => Contributor) public contributors;

  event LogNewOraclizeQuery(string description);

  function setState(State state) public onlyOwner {
    require(state == State.Sale || state == State.Paused || state == State.Finished);

    currentState = state;
  }

  function __callback(bytes32 myId, string result) public {
    if (msg.sender != oraclize_cbAddress()) revert();

    uint256 amountUSD = contributors[myId].amount * parseInt(result);
    uint256 amountToken = amountUSD * parseInt(result) * 100 * (10 ** uint256(decimals));

    require(totalRaisedUSD + amountUSD <= hardCap);
    require(totalRaisedToken + amountToken <= totalSupply);

    totalRaisedUSD += amountUSD;
    totalRaisedToken += amountToken;

    balances[contributors[myId].contributor] += amountToken;
  }

  function () public payable {
    require(currentState == State.Sale);

    if (oraclize_getPrice("URL") > address(this).balance) {
      emit LogNewOraclizeQuery("Oraclize query was NOT sent, please add some ETH to cover for the query fee");
    } else {
      emit LogNewOraclizeQuery("Oraclize query was sent, standing by for the answer..");
      bytes32 queryId = oraclize_query("URL", "json(https://api.gdax.com/products/ETH-USD/ticker).price");
      contributors[queryId] = Contributor(msg.sender, msg.value);
    }
  }

}

